import React, { useState } from 'react'
import {Box, Button, Container, FormControl ,TextField, Radio, RadioGroup, FormControlLabel, FormLabel, Select,MenuItem, InputLabel} from '@mui/material'
// import axios from 'axios'
import { SketchPicker } from 'react-color';
import "./settings.css";
import SettingsIcon from '@mui/icons-material/Settings';
import { addSetting } from "../../redux/ApiCalls";
import {useDispatch} from 'react-redux'



const ScaleSettings = () => {
  const[direction, setDirection] = useState("")
  const[color, setColor] = useState("")
  const[colorHexCode, setColorHexCode] = useState("");                                                                                      
  const[timing, setTiming] = useState("")
  const[time, setTime] = useState("")
  const[label, setLabel] = useState("")
  const[labelA, setLabelA] = useState("")
  const[labelB, setLabelB] = useState("")
  const[view, setView] = useState(true)
