        <div className='scale-container' style={{ display: `${direction === "horizontal" ? "flex" : "block"}`}}>
          {
            scale.map(item=>(
              <div className='scale-box' key={item} value={item} style={{backgroundColor:`${colorHexCode}`}} onClick={()=>saveScore(item)}>{item}</div>
            ))
          }
        </div>

