import React , {useState} from 'react'
import './inputType.css'
import styled, { keyframes } from 'styled-components';
import { bounceInRight, bounceInLeft } from 'react-animations';
import { FormControl ,Radio, RadioGroup, FormControlLabel, FormLabel} from '@mui/material'


const bounceRight = keyframes`${bounceInRight}`;
const bounceLeft = keyframes`${bounceInLeft}`;

const BouncyRight = styled.div`
  animation: 3s ${bounceRight};
`;

const BouncyLeft = styled.div`
  animation: 3s ${bounceLeft};
`;

const InputType = () => {
    const [inputType, setInputType] = useState("")
    const [role, setRole] = useState("")

    console.log(role)
  return (
    <div>
        <h1>Welcome To Nps Scale</h1>
        {/* <h1>Are you a Front End Dev or Client</h1> */}
        <div className="formInput">
            <FormControl>
                <FormLabel id="demo-row-radio-buttons-group-label" style={{fontWeight:"bold", color: "black"}}>Are you a Front End Dev or Client</FormLabel>
                <RadioGroup
                    row
                    aria-labelledby="demo-row-radio-buttons-group-label"
                    name="row-radio-buttons-group"
                    onChange={(e)=>setInputType(e.target.value)}
                    style={{display:"flex", justifyContent:"center"}}
                >
                    <BouncyLeft>
                    <FormControlLabel value="front end programmer" control={<Radio />} label="Front End Programmer" />
                    </BouncyLeft>
                    <BouncyRight>
                    <FormControlLabel value="client" control={<Radio />} label="Client" />
                    </BouncyRight>

                </RadioGroup>
            </FormControl>
        </div>
        {
            inputType && 

            <div className="formInput">
                <FormControl>
                    <FormLabel id="demo-row-radio-buttons-group-label" style={{fontWeight:"bold", color: "black"}}>Are you an Admin or User</FormLabel>
                    <RadioGroup
                        row
                        aria-labelledby="demo-row-radio-buttons-group-label"
                        name="row-radio-buttons-group"
                        onChange={(e)=>setRole(e.target.value)}
                        style={{display:"flex", justifyContent:"center"}}
                    >
                        <BouncyLeft>
                            <FormControlLabel value="admin" control={<Radio />} label="Admin" />
                        </BouncyLeft>
                        <BouncyRight>
                            <FormControlLabel value="user" control={<Radio />} label="User" />
                        </BouncyRight>

                    </RadioGroup>
                </FormControl>
            </div>

        }
    </div>
  )
}

export default InputType
