import React, { useState } from 'react'
import {Box, Button, Container, FormControl ,TextField, Radio, RadioGroup, FormControlLabel, FormLabel, Select,MenuItem} from '@mui/material'
import axios from 'axios'
import { SketchPicker } from 'react-color';


const ScaleSettings = () => {
  const[direction, setDirection] = useState("")
  const[color, setColor] = useState("")
  const [colorHexCode, setColorHexCode] = useState('#000000');
  const[timing, setTiming] = useState("")
  const[time, setTime] = useState(null)


  const saveSettings = async(e)=>{
    e.preventDefault()
    const newSetting ={
      // logo,
      // brand_name : brand,
      // product,
      // link,
      // location,
      // promotion_caption:promotion
    }


    try{
      const res = await axios.post("http://127.0.0.1:8000/nps-scale-settings/", newSetting)
    //   window.location.replace(`/survey_qrcode/${res.data.qrcode_id}`)
    }catch(err){
      console.log(err)
    }
  }



  return (
    <Box>
      <Container sx={{display: 'flex',flexDirection: 'column', width:'400px', marginTop:'15px',padding:"40px" ,marginTop:'15px',borderRadius: "10px",border:"5px solid grey"}}>
        <form onSubmit={saveSettings}>
          <div className="formInput">
            <FormControl>
                <FormLabel id="demo-radio-buttons-group-label">Direction</FormLabel>
                <RadioGroup
                    aria-labelledby="demo-radio-buttons-group-label"
                    defaultValue="female"
                    name="radio-buttons-group"
                >
                    <FormControlLabel value="vertical" control={<Radio />} label="Vertical" />
                    <FormControlLabel value="horizontal" control={<Radio />} label="Horizontal" />
                </RadioGroup>
            </FormControl>
          </div>


          <div className="formInput">
            <label>Color : </label>
            <FormControl variant="filled" sx={{ mt: 2, minWidth: 120 }}>
            <Select
                labelId="demo-simple-select-filled-label"
                id="demo-simple-select-filled"
                // value={inputs.cat}
                // onChange={handleUpdate}
                name="cat"
              > 
                <MenuItem value="True">Yes</MenuItem>
                <MenuItem value="False">No</MenuItem>
            </Select>
            </FormControl>
          </div>

          {

            color === "True" && 
            <div className="formInput">
              <label>Pic Color</label>
              <SketchPicker color={colorHexCode} onChange={e => setColorHexCode(e.hex)} />
            </div> 
          }

          <div className="formInput">
            <label>Time : </label>
            <Select
                labelId="demo-simple-select-filled-label"
                id="demo-simple-select-filled"
                // value={inputs.cat}
                // onChange={handleUpdate}
                name="cat"
              > 
                <MenuItem value="True">Yes</MenuItem>
                <MenuItem value="False">No</MenuItem>
            </Select>
          </div>

          {

            time === "True" && 
            <div className="formInput">
              <label>Set time (sec) : </label>
              <TextField type="number" sx={{marginTop:'15px'}} id="filled-basic" placeholder='Enter Your Brand Name' variant="filled" />
            </div> 
          }

          <Button variant="contained" color="success" sx={{marginTop:'15px'}}>Save & Generate</Button>
        </form>


      </Container>
    </Box>
  )
}

export default ScaleSettings
