import React, { useMemo, useState, useEffect } from 'react'
import {Box, Button, Container, FormControl ,InputLabel,Select,MenuItem,TextField} from '@mui/material'
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline'
import axios from 'axios'
import { SketchPicker } from 'react-color';


const ScaleSettings = () => {
  const[brand, setBrand] = useState("")
  const[product, setProduct] = useState("")
  const[products, setProducts] = useState([])
  const[link, setLink] = useState("")
  const[location, setLocation] = useState("")
  const[promotion, setPromotion] = useState("")
  const [colorHexCode, setColorHexCode] = useState('#000000');



  useEffect(()=>{
    const fetchProd = async()=>{
      const res = await axios.get("http://127.0.0.1:8000/all/products")
      setProducts(res.data)
      // console.log(res)
    }
    fetchProd()
  },[])


  const saveSettings = async(e)=>{
    e.preventDefault()
    const newSetting ={
      // logo,
      // brand_name : brand,
      // product,
      // link,
      // location,
      // promotion_caption:promotion
    }


    try{
      const res = await axios.post("http://127.0.0.1:8000/nps-scale-settings/", newSetting)
    //   window.location.replace(`/survey_qrcode/${res.data.qrcode_id}`)
    }catch(err){
      console.log(err)
    }
  }



  return (
    <Box>
      <Container sx={{display: 'flex',flexDirection: 'column', width:'400px', marginTop:'15px',padding:"40px" ,marginTop:'15px',borderRadius: "10px",border:"5px solid grey"}}>
        <form onSubmit={saveSettings}>
          <div className="formInput">
            <label>Direction</label>
            <FormControl>
                <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                <RadioGroup
                    aria-labelledby="demo-radio-buttons-group-label"
                    defaultValue="female"
                    name="radio-buttons-group"
                >
                    <FormControlLabel value="vertical" control={<Radio />} label="Vertical" />
                    <FormControlLabel value="horizontal" control={<Radio />} label="Horizontal" />
                </RadioGroup>
            </FormControl>
          </div>


          <div className="formInput">
            <label>Color : </label>
            <FormControl>
                <FormLabel id="demo-radio-buttons-group-label">Color</FormLabel>
                <RadioGroup
                    aria-labelledby="demo-radio-buttons-group-label"
                    defaultValue="female"
                    name="radio-buttons-group"
                >
                    <FormControlLabel value="True" control={<Radio />} label="Yes" />
                    <FormControlLabel value="False" control={<Radio />} label="No" />
                </RadioGroup>
            </FormControl>
          </div>

          {

            color === "True" && 
            <div className="formInput">
              <label>Pic Color</label>
              <SketchPicker color={colorHexCode} onChange={e => setColorHexCode(e.hex)} />
            </div> 
          }

          <div className="formInput">
            <label>Time : </label>
            <FormControl>
                <FormLabel id="demo-radio-buttons-group-label">Time</FormLabel>
                <RadioGroup
                    aria-labelledby="demo-radio-buttons-group-label"
                    defaultValue="female"
                    name="radio-buttons-group"
                >
                    <FormControlLabel value="True" control={<Radio />} label="Yes" />
                    <FormControlLabel value="False" control={<Radio />} label="No" />
                </RadioGroup>
            </FormControl>
          </div>

          {

            time === "True" && 
            <div className="formInput">
              <label>Set time (sec) : </label>
              <TextField type="number" sx={{marginTop:'15px'}} id="filled-basic" placeholder='Enter Your Brand Name' variant="filled" onChange={(e)=>setBrand(e.target.value)}/>
            </div> 
          }

          <Button variant="contained" color="success" sx={{marginTop:'15px'}}>Save & Generate</Button>
        </form>


      </Container>
    </Box>
  )
}

export default ScaleSettings
