import React, { useState } from 'react'
import {Box, Button, Container, FormControl ,TextField, Radio, RadioGroup, FormControlLabel, FormLabel, Select,MenuItem, InputLabel} from '@mui/material'
import axios from 'axios'
import { SketchPicker } from 'react-color';
import "./settings.css";
// import {SettingsIcon} from '@mui/icons-material/';


const ScaleSettings = () => {
  const[direction, setDirection] = useState("")
  const[color, setColor] = useState("")
  const [colorHexCode, setColorHexCode] = useState('#000000');
  const[timing, setTiming] = useState("")
  const[time, setTime] = useState("")
  const[view, setView] = useState(false)
  const[settings, setSettings] = useState({})

  console.log(settings.direction)
  const saveSettings = async(e)=>{
    e.preventDefault()
    const newSetting ={
      direction,color, hex_color:colorHexCode,timing, time
    }
    try{
      const res = await axios.post("http://127.0.0.1:8000/nps-scale-settings/", newSetting)
      setSettings(res.data)
      setView(true)
    }catch(err){
      console.log(err)
    }
  }



  return (
    <Box>
      {
        view ?
        <Container sx={{display: 'flex',flexDirection: 'column', justifyContent:"center", width:'400px', marginTop:'15px',padding:"10px 40px" ,borderRadius: "10px",border:"2px solid skyblue"}}>
          <div>
            Settings
          </div>
          <div className='scale-container' style={{backgroundColor:`${settings.hex_color}`}}>
            {
              settings.direction === "vertical" ?

                settings.scale.scale.map(item=>(
                    <div className='scale-content'>
                      <div className='scale-box'>{item}</div>
                      {/* <div className='scale-label'></div> */}
                    </div>

                ))

                :
                <div className='scale-container-hor'>
                  settings.scale.scale.map(item=>(
                      <div className='scale-content'>
                        <div className='scale-box'>{item}</div>
                        {/* <div className='scale-label'></div> */}
                      </div>
                  ))
                </div>

            }


          </div>
        </Container>


        :

        <Container sx={{display: 'flex',flexDirection: 'column', justifyContent:"center", width:'400px', marginTop:'15px',padding:"10px 40px" ,borderRadius: "10px",border:"2px solid skyblue"}}>
          <h3 className='title'>Settings</h3>
          <form onSubmit={saveSettings}>
            <div className="formInput">
              <FormControl>
                  <FormLabel id="demo-row-radio-buttons-group-label">Direction</FormLabel>
                  <RadioGroup
                      row
                      aria-labelledby="demo-row-radio-buttons-group-label"
                      name="row-radio-buttons-group"
                      onChange={(e)=>setDirection(e.target.value)}
                  >
                      <FormControlLabel value="vertical" control={<Radio />} label="Vertical" />
                      <FormControlLabel value="horizontal" control={<Radio />} label="Horizontal" />
                  </RadioGroup>
              </FormControl>
            </div>


            <div className="formInput">
              <label>Color : </label>
              <FormControl variant="filled" sx={{ width: 220 }}>
                <InputLabel id="demo-simple-select-filled-label">Color</InputLabel>
                <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    // value={inputs.cat}
                    onChange={(e)=>setColor(e.target.value)}
                    name="cat"
                  > 
                    <MenuItem value="True">Yes</MenuItem>
                    <MenuItem value="False">No</MenuItem>
                </Select>
              </FormControl>
            </div>

            {

              color === "True" && 
              <div className="formInput">
                <label>Pick Color:</label>
                <SketchPicker color={colorHexCode} onChange={e => setColorHexCode(e.hex)} />
              </div> 
            }

            <div className="formInput">
              <label>Time: </label>
              <FormControl variant="filled" sx={{ width: 220 }}>
                <InputLabel id="demo-simple-select-filled-label">Time</InputLabel>
                <Select
                    labelId="demo-simple-select-filled-label"
                    id="demo-simple-select-filled"
                    // value={inputs.cat}
                    onChange={(e)=>setTiming(e.target.value)}
                    name="cat"
                  > 
                    <MenuItem value="True">Yes</MenuItem>
                    <MenuItem value="False">No</MenuItem>
                </Select>
              </FormControl>
            </div>

            {
              timing === "True" && 
              <div className="formInput">
                <label>Set time(sec) : </label>
                <TextField type="number" sx={{width:220}} id="filled-basic" placeholder='Time in seconds' variant="filled"  onChange={(e)=>{e.target.value > 1 && setTime(e.target.value)}}/>
              </div> 
            }

            <Button variant="contained" type='submit' color="primary" sx={{margin:'auto', display: "flex", justifyContent: "center", alignItems:"center"}}>Save & Generate</Button>
          </form>


        </Container>

      }





    </Box>
  )
}

export default ScaleSettings
