import React , {useState} from 'react'
import './inputType.css'
import styled, { keyframes } from 'styled-components';
import { bounceInRight } from 'react-animations';
import { FormControl ,Radio, RadioGroup, FormControlLabel, FormLabel} from '@mui/material'


const bounceAnimation = keyframes`${bounceInRight}`;

const BouncyDiv = styled.div`
  animation: 1s ${bounceAnimation};
`;

const InputType = () => {
    const [inputType, setInputType] = useState("")
  return (
    <div>
        <h1>Welcome To Nps Scale</h1>
        {/* <h1>Are you a Front End Dev or Client</h1> */}
        <div className="formInput">
              <FormControl>
                  <FormLabel id="demo-row-radio-buttons-group-label" style={{fontWeight:"bold", color: "black"}}>Are you a Front End Dev or Client</FormLabel>
                  <RadioGroup
                      row
                      aria-labelledby="demo-row-radio-buttons-group-label"
                      name="row-radio-buttons-group"
                      onChange={(e)=>setInputType(e.target.value)}
                      style={{display:"flex", justifyContent:"center"}}
                  >
                      <BouncyDiv>
                        <FormControlLabel value="vertical" control={<Radio />} label="Vertical" />
                      </BouncyDiv>
                      <FormControlLabel value="horizontal" control={<Radio />} label="Horizontal" />
                  </RadioGroup>
              </FormControl>
            </div>
        {/* <BouncyDiv></BouncyDiv> */}
    </div>
  )
}

export default InputType
